-- title:  Platformer Demo
-- author: trelemar / pkh
-- desc:   Demo made for tutorial @ infinitelimit.net
-- script: lua
-- input:  gamepad

function solid(x, y)
	return solids[mget((x)//8, (y)//8)]
end

function init()
	solids={[1]=true, [3]=true}
	p={
		x=120,
		y=68,
    }
	v={
        x=0,  --Velocity X
		y=0,  --Velocity Y
	}
end

init()

function TIC()
	if btn(2) then v.x=-1
	elseif btn(3) then v.x=1
	else v.x=0
	end

	if solid(p.x+v.x, p.y+v.y) or solid(p.x+7+v.x, p.y+v.y) or solid(p.x+v.x, p.y+7+v.y) or solid(p.x+7+v.x, p.y+7+v.y) then
		v.x=0
	end

	if solid(p.x, p.y+8+v.y) or solid(p.x+7, p.y+8+v.y) then
		v.y=0
	else
		v.y=v.y+0.2
	end

	if v.y==0 and btnp(4) then v.y=-2.5 end

	if v.y<0 and (solid(p.x+v.x, p.y+v.y) or solid(p.x+7+v.x, p.y+v.y)) then
		v.y=0
	end

	p.x=p.x+v.x
	p.y=p.y+v.y

	cls()
	map()
	rect(p.x, p.y, 8, 8, 12)
end

